import React, {useContext, useEffect} from 'react';
import {Col, Row} from "react-bootstrap";
import {Contact} from "../Store/ContactsStore";
import {removeContact} from "../HTTP/contactAPI"
import {observer} from "mobx-react-lite";
import {Context} from "../index";
import {useNavigate} from "react-router-dom";
import User from "../models/User";
const ContactItem = observer(({name,lastname, id, phone, address}:Contact,key) => {
    let navigate = useNavigate();
    console.log(key)
    const context = useContext(Context);
    let contacts = context.con.Contact
    let user = context.us.User;
    let token = user?.token;

    return (
            <div className={'d-flex justify-content-center align-items-center mt-3 contactItem'}>
                <Col md={3}>
                    <div className={'d-flex flex-column'}>
                        <p className={'mb-2'}>
                            Имя: {name}
                        </p>
                        <p className={'mb-2'}>
                            Фамилия: {lastname}
                        </p>

                    </div>
                </Col>
                <Col md={4} className={'d-flex justify-content-center align-items-center'}>
                    <div >
                        Адрес: {address}
                    </div>
                </Col>
                <Col md={3} className={'d-flex justify-content-center align-items-center'}>
                    <div >
                        Телефон: {phone}
                    </div>
                </Col>
                <Col md={1} className={'d-flex justify-content-center align-items-center'}>
                    <div className={'closer'} onClick={()=>{if(token){removeContact(token, id);contacts?.slice(key, 1);}}}>
                    </div>
                </Col>

            </div>
    );
});

export default ContactItem;
