import {makeAutoObservable} from "mobx";


interface User {
    id: number;
    name: string;
    lastname?: string;

}
export default class  UserStore{

    _auth:boolean;
    _user?:User;

    constructor() {
        this._auth = false;

        makeAutoObservable(this);
    };
    setAuth(bool:boolean){
        this._auth = bool;
    };
    setUser(user:User){
        this._user = user;
    };

    get Auth(){
        return this._auth
    };
    get User(){
        return this._user
    };

}
