import React, {useState} from 'react';
import {Button, Col, Form, Row} from "react-bootstrap";
import {registration} from "../HTTP/userAPI";

const Registration = () => {
    const [login, setLogin] = useState<string>('');
    const [email, setEmail] = useState<string>('');
    const [pass, setPass] = useState<string>('');
    const reqReg:number = registration(login,email,pass)
    return (
        <div className={'formField'}>
            <h4 className={'mt-5 text-center'}>Регистрация</h4>
            <Form className={'d-flex flex-column mb-5'}>
                <Row className={'mt-3 justify-content-center align-items-center'}>
                    <Col md={3}>
                        <h4>Логин</h4>
                    </Col>
                    <Col md={5}>
                        <Form.Control  value={login} onChange={(event)=> {setLogin(event.target.value)}} placeholder={'Придумайте логин'} type={'text'} />
                    </Col>
                </Row>
                <Row className={'mt-3 justify-content-center align-items-center'}>
                    <Col md={3}>
                        <h4>Логин</h4>
                    </Col>
                    <Col md={5}>
                        <Form.Control  value={login} onChange={(event)=> {setEmail(event.target.value)}} placeholder={'Введите ваш email'} type={'text'} />
                    </Col>
                </Row>
                <Row className={'mt-3 justify-content-center align-items-center'}>
                    <Col md={3}>
                        <h4>Пароль</h4>
                    </Col>
                    <Col md={5}>
                        <Form.Control  value={pass} onChange={(event)=> {setPass(event.target.value)}} placeholder={'Придумайте пароль'} type={'text'} />
                    </Col>
                </Row>
                <Row className={'justify-content-center mt-4'}>
                    <Button onClick={()=>{reqReg()}} className={'w-auto'} variant="outline-success">Зарегистрироваться</Button>
                </Row>

            </Form>


        </div>
    );
};

export default Registration;
