import React, {useContext, useEffect, useState} from 'react';
import {observer} from "mobx-react-lite";
import ContactItem from "../Components/ContactItem";
import ModalState from "../Components/ModalState";
import {getAllContacts} from "../HTTP/contactAPI";
import {Contact} from "../Store/ContactsStore"

import {Button, Col, Form, Row} from "react-bootstrap";
import {Context} from "../index";
const Account = observer(() => {
    const [search,setSearch] = useState<string>('')
    const context = useContext(Context);
    let user = context.us.User;
    let contacts = context.con
    let token = user?.token;
    let middlewareArr:Contact[]|undefined = [];
    const Search = ()=>{
        let arr:Contact[]|undefined = contacts.Contact;
        let retArr:Contact[] = [];
        if (arr){
            arr.map((item,index)=>{
                let searching = search.toLowerCase();
                let maySearchable = item.name.toLowerCase();
                if (maySearchable.includes(searching)){
                    retArr.push(item);
                    console.log(item)
                }
            })
        }
        middlewareArr = retArr
    }
    useEffect(()=>{
        if (token){
            getAllContacts(token).then((data)=>{
                let arr:Contact[] = data.rows;
                function sort( a:Contact, b:Contact ) {
                    if ( a.name < b.name ){
                        return -1;
                    }
                    if ( a.name > b.name ){
                        return 1;
                    }
                    return 0;
                }

                contacts.setContact(arr.sort(sort))
                console.log(contacts.Contact)
            })
        }

    },[])
    useEffect(()=>{
        console.log(middlewareArr)
    },[search])
        return (
            <>
                <div className={'accountPage'}>
                    <div className={'account '}>
                        <Row className={'w-100'}>
                            <Form>

                                <Col md={6} className={'w-auto d-flex justify-content-center align-items-center'}>
                                    <div className={'iconSearch mr-5'}>

                                    </div>
                                    <Form.Control className={'w-auto'} value={search} onChange={(event)=> {setSearch(event.target.value); Search();}} placeholder={'Введите имя контакта'} type={'text'} />
                                </Col>

                            </Form>
                        </Row>
                        <Row>
                            {middlewareArr?middlewareArr.map((item:Contact) =>

                                <ContactItem key={item.id} name={item.name} id={item.id} lastname={item.lastname} phone={item.phone} address={item.address}/>

                            ):<></>}
                        </Row>
                        {contacts.Contact?contacts.Contact.map((item:Contact) =>

                            <ContactItem key={item.id} name={item.name} id={item.id} lastname={item.lastname} phone={item.phone} address={item.address}/>

                        ):<></>}
                        <ModalState/>
                    </div>
                </div>

            </>
        );


});

export default Account;
