import {makeAutoObservable} from "mobx";

interface Contact {
    id: number;
    name: string;
    lastname: string;
    phone:string;
    address?:string;
}
interface User {
    id: number;
    name: string;
    lastname?: string;

}
export default class UserStore{

    _constacts:Contact[];
    _auth:boolean;
    _user?:User;
    constructor(user:User) {
        this._auth = false;
        this._user = user;
        this._constacts = []
        makeAutoObservable(this);
    };
    setAuth(bool:boolean){
        this._auth = bool;
    };
    setUser(user:User){
        this._user = user;
    };
    setContact(contacts:Contact[]){
        this._constacts = contacts;
    };
    get Auth(){
        return this._auth
    };
    get User(){
        return this._user
    };
    get Contact(){
        return this._constacts;
    };
}
