import React, {useState} from 'react';
import {Button, Col, Form, Row} from "react-bootstrap";

const Auth = () => {
    const [login, setLogin] = useState<string>('');
    const [pass, setPass] = useState<string>('');
    return (
        <div className={'formField'}>
            <h4>Авторизация</h4>
                <Form className={'d-flex flex-column'}>
                    <Row className={'mt-3 justify-content-center align-items-center'}>
                        <Col md={3}>
                            <h4>Логин</h4>
                        </Col>
                        <Col md={5}>
                            <Form.Control name={'comment'} value={login} onChange={(event)=> {setLogin(event.target.value)}} placeholder={'Введите логин'} type={'text'} />
                        </Col>
                    </Row>
                    <Row className={'mt-3 justify-content-center align-items-center'}>
                        <Col md={3}>
                            <h4>Пароль</h4>
                        </Col>
                        <Col md={5}>
                            <Form.Control name={'comment'} value={pass} onChange={(event)=> {setPass(event.target.value)}} placeholder={'Введите пароль'} type={'text'} />
                        </Col>
                    </Row>
                    <Row className={'justify-content-center mt-4'}>
                        <Button className={'w-auto'} variant="outline-success">Войти</Button>
                    </Row>

                </Form>


        </div>
    );
};

export default Auth;
