import React, {useState} from 'react';
import {Col, Form, Row} from "react-bootstrap";

const FormField = () => {
    const [login, setLogin] = useState<string>('');
    const [pass, setPass] = useState<string>('');
    return (
        <div className={'formField'}>

                <Form>
                    <Row className={'mt-3'}>
                        <Col md={3}>
                            <h4>Логин</h4>
                        </Col>
                        <Col>
                            <Form.Control name={'comment'} value={login} onChange={(event)=> {setLogin(event.target.value)}} placeholder={'Введите логин'} type={'text'} />
                        </Col>
                    </Row>
                    <Row className={'mt-3'}>
                        <Col md={3}>
                            <h4>Пароль</h4>
                        </Col>
                        <Col>
                            <Form.Control name={'comment'} value={pass} onChange={(event)=> {setPass(event.target.value)}} placeholder={'Введите пароль'} type={'text'} />
                        </Col>
                    </Row>

                </Form>


        </div>
    );
};

export default FormField;
